mapper.insert:
INSERT INTO user_info (
    name
    , email_addr
    , join_type
    , user_role
    , picture
) VALUES (
    #{name}
    , #{emailAddr}
    , #{joinType}
    , #{userRole}
    , #{picture}
)
ON DUPLICATE KEY UPDATE
    email_addr = #{emailAddr}

    select LAST_INSERT_ID()

INSERT INTO user_login_history (
